import React, { useState } from "react";
import { Button } from "react-bootstrap";

function TimeSlotSection() {
  // Logic to handle what buttons are shown dpeending on what the current selection is
  const [buttons, setButtons] = useState({
    // should default to false in the beginning. Set to true for testing purposes
    "Clock In": true,
    "Take Break": false,
    Lunch: false,
    "Clock Out": false,
    "Return From Break": false,
    "Return From Lunch": false,
  });

  //   const useEffect(() => {
  //     // hit backend and get current status
  //     // run the current status through the business logic
  //   })
  return (
    <div>
      <Button>Check In</Button>
      <Button>Take Break</Button>
      <Button>Take Lunch</Button>
    </div>
  );
}

export default TimeSlotSection;
